#!/bin/bash

# Complete Real-time Bedrock Usage Control System Deployment
# This script orchestrates the entire deployment process

set -e

echo "🚀 Deploying Complete Real-time Bedrock Usage Control System..."

# Configuration
REGION="us-east-1"
DB_INSTANCE_ID="bedrock-usage-db"
DB_USERNAME="bedrock_admin"
SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
CYAN='\033[0;36m'
NC='\033[0m' # No Color

print_status() {
    echo -e "${GREEN}✅ $1${NC}"
}

print_warning() {
    echo -e "${YELLOW}⚠️  $1${NC}"
}

print_error() {
    echo -e "${RED}❌ $1${NC}"
}

print_info() {
    echo -e "${BLUE}ℹ️  $1${NC}"
}

print_step() {
    echo -e "${CYAN}🔄 $1${NC}"
}

# Check prerequisites
check_prerequisites() {
    print_step "Checking prerequisites..."
    
    # Check AWS CLI
    if ! command -v aws &> /dev/null; then
        print_error "AWS CLI is not installed. Please install it first."
        exit 1
    fi
    
    # Check AWS credentials
    if ! aws sts get-caller-identity &> /dev/null; then
        print_error "AWS CLI is not configured. Please run 'aws configure' first."
        exit 1
    fi
    
    # Check MySQL client
    if ! command -v mysql &> /dev/null; then
        print_warning "MySQL client is not installed. You'll need to run the database initialization manually."
    fi
    
    print_status "Prerequisites check completed"
}

# Deploy RDS instance
deploy_rds() {
    print_step "Step 1: Deploying RDS MySQL instance..."
    
    if [ -f "$SCRIPT_DIR/01_create_rds_instance.sh" ]; then
        chmod +x "$SCRIPT_DIR/01_create_rds_instance.sh"
        bash "$SCRIPT_DIR/01_create_rds_instance.sh"
        print_status "RDS deployment completed"
    else
        print_error "RDS deployment script not found: $SCRIPT_DIR/01_create_rds_instance.sh"
        exit 1
    fi
}

# Wait for RDS to be available
wait_for_rds() {
    print_step "Waiting for RDS instance to be available..."
    
    local max_attempts=30
    local attempt=1
    
    while [ $attempt -le $max_attempts ]; do
        local status=$(aws rds describe-db-instances \
            --db-instance-identifier $DB_INSTANCE_ID \
            --query 'DBInstances[0].DBInstanceStatus' \
            --output text 2>/dev/null || echo "not-found")
        
        if [ "$status" = "available" ]; then
            print_status "RDS instance is available"
            return 0
        elif [ "$status" = "not-found" ]; then
            print_error "RDS instance not found"
            return 1
        else
            print_info "RDS status: $status (attempt $attempt/$max_attempts)"
            sleep 30
            ((attempt++))
        fi
    done
    
    print_error "RDS instance did not become available within expected time"
    return 1
}

# Initialize database
initialize_database() {
    print_step "Step 2: Initializing database schema..."
    
    # Get RDS endpoint
    local rds_endpoint=$(aws rds describe-db-instances \
        --db-instance-identifier $DB_INSTANCE_ID \
        --query 'DBInstances[0].Endpoint.Address' \
        --output text)
    
    if [ "$rds_endpoint" = "None" ] || [ -z "$rds_endpoint" ]; then
        print_error "Could not retrieve RDS endpoint"
        return 1
    fi
    
    print_info "RDS Endpoint: $rds_endpoint"
    
    # Prompt for database password
    echo ""
    print_info "Please enter the database password for user '$DB_USERNAME':"
    read -s db_password
    echo ""
    
    if [ -z "$db_password" ]; then
        print_error "Password cannot be empty"
        return 1
    fi
    
    # Test database connection
    print_info "Testing database connection..."
    if command -v mysql &> /dev/null; then
        if mysql -h "$rds_endpoint" -u "$DB_USERNAME" -p"$db_password" -e "SELECT 1;" &> /dev/null; then
            print_status "Database connection successful"
            
            # Run initialization script
            print_info "Running database initialization script..."
            mysql -h "$rds_endpoint" -u "$DB_USERNAME" -p"$db_password" < "$SCRIPT_DIR/08_initialize_database.sql"
            print_status "Database initialization completed"
        else
            print_error "Database connection failed. Please check credentials and network connectivity."
            return 1
        fi
    else
        print_warning "MySQL client not available. Please run the following command manually:"
        echo "mysql -h $rds_endpoint -u $DB_USERNAME -p < $SCRIPT_DIR/08_initialize_database.sql"
    fi
    
    # Store password for later use
    export DB_PASSWORD="$db_password"
    export RDS_ENDPOINT="$rds_endpoint"
}

# Configure database connection
configure_database_connection() {
    print_step "Step 3: Configuring database connection..."
    
    if [ -f "$SCRIPT_DIR/07_configure_database_connection.sh" ]; then
        chmod +x "$SCRIPT_DIR/07_configure_database_connection.sh"
        
        # Modify the script to use our stored password
        export DB_PASSWORD
        export RDS_ENDPOINT
        
        # Run with pre-set password
        echo "$DB_PASSWORD" | bash "$SCRIPT_DIR/07_configure_database_connection.sh"
        print_status "Database connection configuration completed"
    else
        print_error "Database connection script not found: $SCRIPT_DIR/07_configure_database_connection.sh"
        return 1
    fi
}

# Deploy real-time logger
deploy_realtime_logger() {
    print_step "Step 4: Deploying real-time request logger..."
    
    if [ -f "$SCRIPT_DIR/06_deploy_realtime_logger.sh" ]; then
        chmod +x "$SCRIPT_DIR/06_deploy_realtime_logger.sh"
        bash "$SCRIPT_DIR/06_deploy_realtime_logger.sh"
        print_status "Real-time logger deployment completed"
    else
        print_error "Real-time logger script not found: $SCRIPT_DIR/06_deploy_realtime_logger.sh"
        return 1
    fi
}

# Test the system
test_system() {
    print_step "Step 5: Testing the system..."
    
    # Test Lambda function
    print_info "Testing Lambda function..."
    local test_event='{
        "version": "0",
        "id": "test-event-id",
        "detail-type": "AWS API Call via CloudTrail",
        "source": "aws.bedrock",
        "account": "'$(aws sts get-caller-identity --query Account --output text)'",
        "time": "'$(date -u +%Y-%m-%dT%H:%M:%SZ)'",
        "region": "'$REGION'",
        "detail": {
            "eventVersion": "1.08",
            "userIdentity": {
                "type": "IAMUser",
                "principalId": "AIDACKCEVSQ6C2EXAMPLE",
                "arn": "arn:aws:iam::'$(aws sts get-caller-identity --query Account --output text)':user/test-user-1",
                "accountId": "'$(aws sts get-caller-identity --query Account --output text)'",
                "userName": "test-user-1"
            },
            "eventTime": "'$(date -u +%Y-%m-%dT%H:%M:%SZ)'",
            "eventSource": "bedrock-runtime.amazonaws.com",
            "eventName": "InvokeModel",
            "awsRegion": "'$REGION'",
            "sourceIPAddress": "203.0.113.12",
            "userAgent": "aws-cli/2.0.0",
            "requestParameters": {
                "modelId": "anthropic.claude-3-5-sonnet-20241022-v2:0"
            },
            "responseElements": {
                "usage": {
                    "inputTokens": 100,
                    "outputTokens": 200
                }
            },
            "requestID": "test-request-id-123",
            "eventID": "test-event-id-456",
            "eventType": "AwsApiCall",
            "recipientAccountId": "'$(aws sts get-caller-identity --query Account --output text)'",
            "serviceEventDetails": null
        }
    }'
    
    # Invoke Lambda function with test event
    local response=$(aws lambda invoke \
        --function-name "bedrock-realtime-request-logger" \
        --payload "$test_event" \
        --region $REGION \
        /tmp/lambda-response.json 2>&1)
    
    if [ $? -eq 0 ]; then
        print_status "Lambda function test successful"
        print_info "Response: $(cat /tmp/lambda-response.json)"
        rm -f /tmp/lambda-response.json
    else
        print_warning "Lambda function test failed: $response"
    fi
    
    # Test database connectivity from Lambda
    print_info "Testing database connectivity from Lambda..."
    local db_test_event='{
        "test": "database_connection"
    }'
    
    aws lambda invoke \
        --function-name "bedrock-realtime-request-logger" \
        --payload "$db_test_event" \
        --region $REGION \
        /tmp/db-test-response.json &> /dev/null
    
    if [ $? -eq 0 ]; then
        print_status "Database connectivity test completed"
        rm -f /tmp/db-test-response.json
    else
        print_warning "Database connectivity test may have issues"
    fi
}

# Generate deployment summary
generate_summary() {
    print_step "Generating deployment summary..."
    
    local account_id=$(aws sts get-caller-identity --query Account --output text)
    local rds_endpoint=$(aws rds describe-db-instances \
        --db-instance-identifier $DB_INSTANCE_ID \
        --query 'DBInstances[0].Endpoint.Address' \
        --output text 2>/dev/null || echo "Not found")
    
    echo ""
    echo "🎉 Real-time Bedrock Usage Control System Deployment Complete!"
    echo ""
